function ukCoverImageFix(){$($("img[data-uk-cover]")).each(function(){var a=$(this).parents(".uk-cover-container");$(this).css("min-height",a.height())})}function setRatioHeight(){$($("[data-ratio-height]")).each(function(){var d=$(this).data("ratio-height").match(/(\d+):(\d+)/);if(null!==d){var b=d[1],c=d[2];b&&c&&$(this).height(Math.round($(this).width()/b*c))}else{console.error("data-ratio-height syntaxis eror")}})}function setRatioMinHeight(){$($("[data-ratio-min-height]")).each(function(){var d=$(this).data("ratio-min-height").match(/(\d+):(\d+)/);if(null!==d){var b=d[1],c=d[2];b&&c&&$(this).css("min-height",Math.round($(this).width()/b*c))}else{console.error("data-ratio-min-height syntaxis eror")}})}$(document).ready(function(){ukCoverImageFix();setRatioHeight();setRatioMinHeight()});$(window).resize(function(){ukCoverImageFix();setRatioMinHeight();setRatioHeight()});